mapper.select:
select
count(1)
from operator_log


	operator = #{userKey,jdbcType=VARCHAR}


	AND createtime >= #{startDate,jdbcType=TIMESTAMP}


	AND createtime <= #{endDate,jdbcType=TIMESTAMP}

select

 from operator_log


		operator = #{userKey,jdbcType=VARCHAR}


		AND createtime >= #{startDate,jdbcType=TIMESTAMP}


		AND createtime <= #{endDate,jdbcType=TIMESTAMP}


order by createtime desc

limit #{offset},#{limit};
